import React, { useState } from 'react'
import Hamburger from './Hamburger.js'

const DEFAULT_DESCRIPTION = ''

export default function Products({ addToCart }) {
    const [descript, setDescript] = useState(DEFAULT_DESCRIPTION)
    const [display, setDisplay] = useState(false)
    const [products] = useState([
        {
          name: 'AA Battery',
          price: 2.99,
          description: "Get all your battery needs from Duracell",
          image: "data:image/png;base64,"
          
        },
        {
          name: 'Hammer',
          price: 6.99,
          description: "Beat that piece of metal you need with this max-axe hammer",
          image: "data:image/png;base64,"
          
        },
        {
            name: 'Teddy Bear',
            price: 3.99,
            description: "Cuddle MR.Snuggles on warm nights",
            image: "data:image/jpeg;base64,"
            
        },
        {
            name: 'Car',
            price: 999.99,
            description: "Get around town fast in this Lada 900",
            image: "data:image/jpeg;base64,"
            
        }
    ])

    const renderInfo = (product) => {
      let ps = document.getElementById('whole')
      ps.style.display = 'none'
      let show = document.getElementById('description_display')
      if (!display) {
        show.style.display = 'block'
        setDisplay(true)
        setDescript(product.description)
      } else {
        show.style.display = 'none'
        setDisplay(false)
        }
    }

    const goBack = () => {
      let ps = document.getElementById('whole')
      ps.style.display = 'block'
      let show = document.getElementById('description_display')
      show.style.display = 'none'
    }

    return (
        <>
          <h1>Products</h1>
          <div className="products" id="whole">
            {products.map((product, idx) => (
              <div className="product" key={idx}>
                <h2>{product.name}</h2>
                <h3>${product.price}</h3>
                <img src={product.image} alt={product.name}/>
                <button onClick={() => addToCart(product)}>
                  Add to cart
                </button>
                <section onClick={() => renderInfo(product)}>
                    <Hamburger/>
                </section>
              </div>
            ))}
          </div>
          <section id="description_display">
                Description: {descript}
                <div onClick={() => goBack()}>
                  <Hamburger />
                </div>
                  
            </section>
        </>
    )
}
